package com.playhaven.resources.files;

import android.util.DisplayMetrics;

import com.playhaven.resources.PHNinePatchResource;

/** Is the background image for the badge notification view and a nine patch drawable. The b64 encoded data you see below is the result of aapt compilation and not the original image (we need 9patch chunk).
 * We have two resolutions: high and low for different screen sizes. All other densities are left up to the default scaling.
 * @author samstewart
 *
 */
public class PHBadgeImageResource extends PHNinePatchResource {
	public PHBadgeImageResource() {
		//standard resolution
		String standardRes = "";
		super.setDataStr(DisplayMetrics.DENSITY_DEFAULT, standardRes);
		
		//greater resolution (1.5x)
		String highRes = "";
		super.setDataStr(DisplayMetrics.DENSITY_HIGH, highRes);
	}
}
